orangefoxpath="https://download.wetransfer.com/eugv/de7b84a97bc211cf0b9f472648d4f63920210811235912/d9638e1af17ec01dabed161438435ea1fd998a15/orangfox.tar.zst?token=eyJhbGciOiJIUzI1NiJ9..mRIiRMSihZXk7IOvufBF_zkZtxmH7BhA62MJrrVvCVw&cf=y"
apt install git aria2 rsync curl sudo wget -y
wget -nv -O - "${orangefoxpath}" | tar -zx
git clone https://gitlab.com/OrangeFox/misc/scripts
bash scripts/setup/android_build_env.sh
bash scripts/setup/install_android_sdk.sh
cd fox_10.0
git clone https://github.com/lateautumn233/android_device_xiaomi_lmi-ofrp device/xiaomi/apollo
wget https://gitlab.com/OrangeFox/vendor/recovery/-/raw/master/security/miui.x509.pem?inline=false -o vendor/recovery/security/miui.x509.pem
source build/envsetup.sh
export ALLOW_MISSING_DEPENDENCIES=true
export FOX_USE_TWRP_RECOVERY_IMAGE_BUILDER=1
export OF_PATCH_AVB20=1
export LC_ALL="C"
lunch omni_apollo-eng && mka recoveryimage